-- DROP TABLE IF EXISTS `dc_databuff_monitor_preset`;
CREATE TABLE `dc_databuff_monitor_preset` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `api_key` varchar(64) NOT NULL COMMENT '租户 apiKey',
  `name` varchar(255) NOT NULL COMMENT '监控名称',
  `tags` text NOT NULL COMMENT '监控标签',
  `monitor_object` varchar(255) NOT NULL COMMENT '监控对象',
  `classification` varchar(20) NOT NULL COMMENT '监控类型',
  `type` varchar(15) NOT NULL COMMENT '检测类型',
  `priority` tinyint(1) NOT NULL COMMENT '优先级',
  `multi` tinyint(1) NOT NULL COMMENT '是否分组0否1是',
  `message` text COMMENT '监控事件信息模版',
  `query` text NOT NULL COMMENT '监控查询条件',
  `options` longtext NOT NULL COMMENT '监控参数',
  `params` longtext COMMENT '监控其他参数',
  `monitor_notice` longtext COMMENT '告警通知',
  `monitor_public` tinyint(1) NOT NULL COMMENT '是否公共的0F 1T',
  `default_open` tinyint(1) NOT NULL COMMENT '是否默认开启0F 1T',
  `create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `update_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `rule_name` varchar(255) DEFAULT NULL COMMENT '监控规则名称',
  `user_id` mediumint(9) DEFAULT NULL COMMENT '创建用户ID',
  `service_ids` text COMMENT '服务id列表',
  `service_instances` text COMMENT '服务实例列表',
  `target` varchar(100) DEFAULT NULL COMMENT '监控目标类型',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE KEY `api_key_metric` (`api_key`,`name`) USING BTREE,
  KEY `api_key` (`api_key`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=100 DEFAULT CHARSET=utf8mb4 ROW_FORMAT=DYNAMIC COMMENT='预置监控';

